import React, { useState } from "react";
import { useForm } from "react-hook-form";

function Offer(props) {
  const [formStep, setFormStep] = useState(0);
  const {
    register,
    handleSubmit,
  } = useForm({
    mode: "onBlur",
  });
  const onSubmit = (item) => {
    fetch("http://localhost:8080/application/apply", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({
        applicationId: item.applicationId,
        requestedAmount: item.requestedAmount,
        totalAmount: item.totalAmount,
        term: item.term,
        monthlyPayment: item.monthlyPayment,
        rate: item.rate,
        isInsuranceEnabled: item.isInsuranceEnabled,
        isSalaryClient: item.isSalaryClient,
      }),
    })
      .then((response) => response.json())
      .then((res) => console.log(res));

    localStorage.setItem(
      "applicationId",
      item.applicationId
    );
    setFormStep((cur) => cur + 1);
  };
  return (
    
    <>
    {formStep === 0 && (
       <div className="offer-grid">
       {props.offers.map((item) => (
         <>
           <form onSubmit={handleSubmit(onSubmit)}>
             <div className="item1-offer">
               <img
                 className="surpise-img"
                 src="./images/SurpriseImage.png"
                 alt=""
               />
               <p key={item.applicationId}>
                 Requested amount: {item.requestedAmount} ₽
               </p>
               <p key={item.applicationId}>
                 Total amount:
                 {item.totalAmount} ₽
               </p>
               <p key={item.applicationId}>For {item.term} months</p>
               <p key={item.applicationId}>
                 Monthly payment: {item.monthlyPayment} ₽
               </p>
               <p key={item.applicationId}>
                 Rate:
                 {item.rate} %
               </p>
               <p>
                 Insurance included{" "}
                 <img src="./assets/error.svg" alt="" />
               </p>
               <p>
                 Salary client <img src="./assets/error.svg" alt="" />
               </p>
               <button
                 className="btn-offer"
                 key={item.applicationId}
               >
                 Select
               </button>
             </div>
           </form>
         </>
       ))}
     </div>
    )}
    {formStep === 2 && <Result />}
    </>
  );
}

export default Offer;
