import React, { useState, useRef } from "react";
import ReactCodeInput from "react-code-input";

import { Audio } from "react-loader-spinner";

function Code() {
  const ref = useRef(null);
  const CORRECT_PIN_CODE = "1243";
  const pathname = window.location.pathname;
  let id = pathname.split("/");
  id = id[2];
  const props = {
    inputStyle: {
      fontFamily: 'monospace',
      margin:  '4px',
      MozAppearance: 'textfield',
      width: '15px',
      borderRadius: '3px',
      fontSize: '14px',
      height: '26px',
      paddingLeft: '7px',
      backgroundColor: 'black',
      color: 'lightskyblue',
      border: '1px solid lightskyblue'
    },
    inputStyleInvalid: {
      fontFamily: 'monospace',
      margin:  '4px',
      MozAppearance: 'textfield',
      width: '15px',
      borderRadius: '3px',
      fontSize: '14px',
      height: '26px',
      paddingLeft: '7px',
      backgroundColor: 'black',
      color: 'red',
      border: '1px solid red'
    }
  }
  let [loading, setLoading] = useState(false);
  const [formStep, setFormStep] = useState(0);
  const [isPinCodeValid, setIsPinCodeValid] = useState(true);
  const [pinCode, setPinCode] = useState("");
  const [btnIsPressed, setBtnIsPressed] = useState(false);

  const handlePinChange = (pinCode) => {
    setPinCode(pinCode);
    setBtnIsPressed(false);
    const isPinCodeValid = pinCode === CORRECT_PIN_CODE;

    setBtnIsPressed(true);
    setIsPinCodeValid(isPinCodeValid);
    if (!isPinCodeValid) {
      setPinCode(" ");
      ref.current.innerHTML = "Invalid confirmation code";
    }
    if (isPinCodeValid) {
      fetch(`http://localhost:8080/document/${id}/sign/code`, {
        method: "POST",
        headers: {
          "Access-Control-Allow-Origin": "*",
          "Access-Control-Allow-Methods": "GET,PUT,POST,DELETE,PATCH,OPTIONS",
          "Content-Type": "application/json",
        },
        body: pinCode,
      })
        .then((response) => response.json())
        .then((res) => console.log(res));

      setLoading(true);
      setTimeout(() => {
        setLoading(false);
      }, 5000);
      setFormStep((cur) => cur + 1);
    }
  };

  return (
    <>
      {loading ? (
        <Audio color="#003CFF" />
      ) : (
        <>
          {formStep === 0 && (
            <div className="code">
              <h1>Please enter confirmation code</h1>
              <ReactCodeInput
                id="pinCode"
                type="password"
                isValid={isPinCodeValid}
                fields={4}
                onChange={handlePinChange}
                value={pinCode}
                {...props}
              />
              <br/>
              <span ref={ref}></span>
            </div>
          )}
          {formStep === 1 && (
            <>
              <img src="/images/SurpriseImage.png" />
              <h1>Congratulations! You have completed your new credit card.</h1>
              <p>
                Your credit card will arrive soon. Thank you for choosing us!
              </p>
              <a href="/">View other offers of our bank</a>
            </>
          )}
        </>
      )}
    </>
  );
}

export default Code;
