import React, { useState } from "react";
import Result from "./Result";

function Offer(props) {
  const [formStep, setFormStep] = useState(1);

  useEffect(() => {
   
    fetch(`http://localhost:8080/admin/application/${id}`, {
      method: "GET",
      headers: {
        "Content-Type": "application/json",
      },
    })
      .then((response) => response.json())
      .then((arr) => {
        for (let i = 0; i < arr.credit.paymentSchedule.length; i++) {
        document.querySelector("#table").innerHTML += `
        <tr>
              <td>${arr.credit.paymentSchedule[i].number}</td>
              <td>${arr.credit.paymentSchedule[i].date}</td>
              <td>${arr.credit.paymentSchedule[i].totalPayment}</td>
              <td>${arr.credit.paymentSchedule[i].interestPayment}</td>
              <td>${arr.credit.paymentSchedule[i].debtPayment}</td>
              <td>${arr.credit.paymentSchedule[i].remainingDebt}</td>
        </tr>
              `;
      } });
   
  }, []);
  
  return (
    <>
      {formStep === 1 && (
        <div className="offer-grid">
          {props.offers.map((item) => (
            <>
                <div className="item1-offer" id="">
                  <img
                    className="surpise-img"
                    src="./images/SurpriseImage.png"
                    alt=""
                  />
                  <p key={item.applicationId}>
                    Requested amount: {item.requestedAmount} ₽
                  </p>
                  <p key={item.applicationId}>
                    Total amount:
                    {item.totalAmount} ₽
                  </p>
                  <p key={item.applicationId}>For {item.term} months</p>
                  <p key={item.applicationId}>
                    Monthly payment: {item.monthlyPayment} ₽
                  </p>
                  <p key={item.applicationId}>
                    Rate:
                    {item.rate} %
                  </p>
                  <p>
                    Insurance included <img src="./assets/error.svg" alt="" />
                  </p>
                  <p>
                    Salary client <img src="./assets/error.svg" alt="" />
                  </p>
                  <button
                    className="btn-offer"
                    key={item.applicationId}
                    onClick={() => {
                      fetch("http://localhost:8080/application/apply", {
                        method: "POST",
                        headers: {
                          "Content-Type": "application/json",
                        },
                        body: JSON.stringify({
                          applicationId: item.applicationId,
                          requestedAmount: item.requestedAmount,
                          totalAmount: item.totalAmount,
                          term: item.term,
                          monthlyPayment: item.monthlyPayment,
                          rate: item.rate,
                          isInsuranceEnabled: item.isInsuranceEnabled,
                          isSalaryClient: item.isSalaryClient,
                        }),
                      })
                        .then((response) => response.json())
                        .then((res) => console.log(res));

                      console.log(JSON.stringify(item));
                      localStorage.setItem("applicationId", item.applicationId);
                      setFormStep((cur) => cur + 1);
                    }}
                  >
                    Select
                  </button>
                </div>
            </>
          ))}
        </div>
      )}
      {formStep === 2 && <Result />}
    </>
  );
}

export default Offer;
