const Table=()=> {
  const pathname = window.location.pathname;
  let id = pathname.split("/");
  id = id[2];

  const [payments, setPayment] = useState([]);
  const columns =  ['NUMBER', 'DATE', 'TOTAL PAYMENT', 'INTEREST PAYMENT', 'DEBT PAYMENT', 'REMAINING DEBT']

  useEffect(() => {
    for (let key in CURRENCY_CODE) {
      fetch(
        `https://currency-exchange.p.rapidapi.com/exchange?from=${key}&to=RUB&q=1.0`,
        options
      )
        .then((res) => {
          return res.json();
        })
        .then((arr) => {
          document.querySelector("#data").innerHTML += `
                <p class="gray">${key}:</p>
                <p class="money">${arr.toFixed(2)}</p>
                `;
        });
    }
  }, []);
  return (
   <>
    <table>
      <thead>
        <tr>
          {columns.map(column =>(
            <th key={column}>{column}</th>
          ))}
        </tr>
      </thead>
      <Content entries={payments} columns={columns}/>
    </table>

   </>
  );
}

const Content = ({entries, columns}) =>{
  return(
    <tbody>
        {entries.map(entrie => (
          <tr key={entrie.id}>
            {columns.map(column =>(
            <td key={column}>{entrie[column]}</td>
          ))}
          </tr>
        ))
          
        }
      </tbody>
  );
};
export default Table;
