import React, { useState, useEffect } from "react";

const Table=()=> {
  const pathname = window.location.pathname;
  let id = pathname.split("/");
  id = id[2];

  const columns =  ['NUMBER', 'DATE', 'TOTAL PAYMENT', 'INTEREST PAYMENT', 'DEBT PAYMENT', 'REMAINING DEBT']

  useEffect(() => {
   
    fetch(`http://localhost:8080/admin/application/${id}`, {
      method: "GET",
      headers: {
        "Content-Type": "application/json",
      },
    })
      .then((response) => response.json())
      .then((arr) => {
        for (let i = 0; i < arr.credit.paymentSchedule.length; i++) {
        document.querySelector("#table").innerHTML += `
        <tr>
              <td>${arr.credit.paymentSchedule[i].number}</td>
              <td>${arr.credit.paymentSchedule[i].date}</td>
              <td>${arr.credit.paymentSchedule[i].totalPayment}</td>
              <td>${arr.credit.paymentSchedule['1'].interestPayment}</td>
              <td>${arr.credit.paymentSchedule['1'].debtPayment}</td>
              <td>${arr.credit.paymentSchedule['1'].remainingDebt}</td>
        </tr>
              `;
              console.log(arr.credit.paymentSchedule['1']);
      } });
   
  }, []);

  return (
   <>
    <table>
      <thead>
        <tr>
          {columns.map(column =>(
            <th key={column}>{column}</th>
          ))}
        </tr>
      </thead>
     <tbody id="table">

     </tbody>
    </table>

   </>
  );
}


export default Table;
