import React, {useRef, useState} from 'react'
import {useForm} from "react-hook-form";

function Support() {
  const ref = useRef(null);
  const [showBanner, setShowBanner] = useState(true);

  const {
    register,
    handleSubmit,
    formState: { errors },
    reset,
    } = useForm({
    mode: "onBlur"
    });

    const onSubmit = (data) => {
      fetch('http://localhost:8080/email', {  
        method: 'POST', 
        body: JSON.stringify(data) 
      })
      .catch(err => {
        if (err){
          ref.current.innerHTML = "<p>Something wrong, try again</p>";
        }
        })

      ref.current.innerHTML = "<p>You are already subscribed to the bank's newsletter</p>";
      reset();

    }




  return (
    <>


    <p className="title-support">Support</p>
    <p className="subtitle-support">Subscribe Newsletter & get</p>
    <p className="bank-news">Bank News</p>
    {showBanner && (
      <form onSubmit={handleSubmit(onSubmit)} className='support-form' ref={ref}>
        <label >
        <input className="email" type="email" placeholder="Your email" {...register('email', { required: true})} />
        </label>
        <input id="btn-support" className="btn-support" type="submit" value="Subscribe" onClick={()=> showBanner}/>
        <div >{errors?.email && <p>Enter your email</p>}</div>
      </form>
    )}
    </>
  )
}

export default Support
