import React, { useState, useEffect } from "react";

const Content = ({entries, columns}) =>{
  return(
    <tbody>
        {entries.map(entrie => (
          <tr key={entries.id}>
            {columns.map(column =>(
            <td key={column}>{payment[column]}</td>
          ))}
          </tr>
        ))
          
        }
      </tbody>
  );
};

const Table=()=> {
  const pathname = window.location.pathname;
  let id = pathname.split("/");
  id = id[2];

  const [payments, setPayment] = useState([]);
  const columns =  ['NUMBER', 'DATE', 'TOTAL PAYMENT', 'INTEREST PAYMENT', 'DEBT PAYMENT', 'REMAINING DEBT']

  useEffect(() => {
    fetch(`http://localhost:8080/admin/application/${id}`, {
      method: "GET",
      headers: {
        "Content-Type": "application/json",
      },
    })
      .then((response) => response.json())
      .then((payments) => setPayment(payments));
  }, []);
  return (
   <>
    <table>
      <thead>
        <tr>
          {columns.map(column =>(
            <th key={column}>{column}</th>
          ))}
        </tr>
      </thead>
      <Content entries={payments} columns={columns}/>
    </table>

   </>
  );
}


export default Table;
