import React, {useState} from "react";

function Document() {
  const pathname = window.location.pathname;
  let id = pathname.split("/");
  id = id[2];

  let [payments, setPayment] = useState({});

  fetch(`http://localhost:8080/admin/application/${id}`, {
    method: "GET",
    headers: {
      "Content-Type": "application/json",
    },
  })
    .then((response) => response.json())
    .then((payment) => setPayment(payment));

  return (
    <>
      <div className="payment">
        <div className="payment-header">
          <h1>Payment Schedule</h1>
          <p>Step 3 of 5</p>
        </div>
        <div className="payment-table">
        {payments.map((item) => (
            <>
                <div className="item1-offer">
                  <img
                    className="surpise-img"
                    src="./images/SurpriseImage.png"
                    alt=""
                  />
                  <p key={item.applicationId}>
                    Requested amount: {item.requestedAmount} ₽
                  </p>
                  <p key={item.applicationId}>
                    Total amount:
                    {item.totalAmount} ₽
                  </p>
                  <p key={item.applicationId}>For {item.term} months</p>
                  <p key={item.applicationId}>
                    Monthly payment: {item.monthlyPayment} ₽
                  </p>
                  <p key={item.applicationId}>
                    Rate:
                    {item.rate} %
                  </p>
                  <p>
                    Insurance included <img src="./assets/error.svg" alt="" />
                  </p>
                  <p>
                    Salary client <img src="./assets/error.svg" alt="" />
                  </p>
                  <button
                    className="btn-offer"
                    key={item.applicationId}
                    onClick={() => {
                      fetch("http://localhost:8080/application/apply", {
                        method: "POST",
                        headers: {
                          "Content-Type": "application/json",
                        },
                        body: JSON.stringify({
                          applicationId: item.applicationId,
                          requestedAmount: item.requestedAmount,
                          totalAmount: item.totalAmount,
                          term: item.term,
                          monthlyPayment: item.monthlyPayment,
                          rate: item.rate,
                          isInsuranceEnabled: item.isInsuranceEnabled,
                          isSalaryClient: item.isSalaryClient,
                        }),
                      })
                        .then((response) => response.json())
                        .then((res) => console.log(res));

                      console.log(JSON.stringify(item));
                      localStorage.setItem("applicationId", item.applicationId);
                      setFormStep((cur) => cur + 1);
                    }}
                  >
                    Select
                  </button>
                </div>
            </>
          ))}
        </div>
      </div>
    </>
  );
}

export default Document;
