import React, { useState, useEffect } from "react";

const Table=()=> {
  const pathname = window.location.pathname;
  let id = pathname.split("/");
  id = id[2];


  const columns =  ['NUMBER', 'DATE', 'TOTAL PAYMENT', 'INTEREST PAYMENT', 'DEBT PAYMENT', 'REMAINING DEBT'];
  const [sortField, setSortField] = useState("");
  const [order, setOrder] = useState("asc");

  useEffect(() => {
   
    fetch(`http://localhost:8080/admin/application/${id}`, {
      method: "GET",
      headers: {
        "Content-Type": "application/json",
      },
    })
      .then((response) => response.json())
      .then((arr) => {
        for (let i = 0; i < arr.credit.paymentSchedule.length; i++) {
        document.querySelector("#table").innerHTML += `
        <tr>
              <td>${arr.credit.paymentSchedule[i].number}</td>
              <td>${arr.credit.paymentSchedule[i].date}</td>
              <td>${arr.credit.paymentSchedule[i].totalPayment}</td>
              <td>${arr.credit.paymentSchedule[i].interestPayment}</td>
              <td>${arr.credit.paymentSchedule[i].debtPayment}</td>
              <td>${arr.credit.paymentSchedule[i].remainingDebt}</td>
        </tr>
              `;
      } });
   
  }, []);

  const handleSortingChange = (column) => {
    const sortOrder =
    accessor === sortField && order === "asc" ? "desc" : "asc";
    setSortField(accessor);
    setOrder(sortOrder);
    handleSorting(accessor, sortOrder);
   };

  return (
   <>
    <table className="payment_table">
      <thead className="payment_header">
        <tr>
          {columns.map(column =>(
            <th key={column} onClick={() => handleSortingChange(column)}>{column}</th>
          ))}
        </tr>
      </thead>
     <tbody id="table">

     </tbody>
    </table>

   </>
  );
}


export default Table;
