import React, { useState, useEffect } from "react";

const Table = () => {
  const pathname = window.location.pathname;
  let id = pathname.split("/");
  id = id[2];

  const columns = [
    { label: "NUMBER", accessor: "number" },
    { label: "DATE", accessor: "date" },
    { label: "TOTAL PAYMENT", accessor: "totalPayment" },
    { label: "INTEREST PAYMENT", accessor: "interestPayment" },
    { label: "DEBT PAYMENT", accessor: "debtPayment" },
    { label: "REMAINING DEBT", accessor: "remainingDebt" },
  ];
  const [sortField, setSortField] = useState("");
  const [order, setOrder] = useState("asc");
  const [tableData, setTableData] = useState([]);

  useEffect(() => {
    fetch(`http://localhost:8080/admin/application/${id}`, {
      method: "GET",
      headers: {
        "Content-Type": "application/json",
      },
    })
      .then((response) => response.json())
      .then((arr) => {
        setTableData(arr.credit.paymentSchedule);
      });

    /* ((arr) => {
        for (let i = 0; i < arr.credit.paymentSchedule.length; i++) {
        document.querySelector("#table").innerHTML += `
        <tr>
              <td>${arr.credit.paymentSchedule[i].number}</td>
              <td>${arr.credit.paymentSchedule[i].date}</td>
              <td>${arr.credit.paymentSchedule[i].totalPayment}</td>
              <td>${arr.credit.paymentSchedule[i].interestPayment}</td>
              <td>${arr.credit.paymentSchedule[i].debtPayment}</td>
              <td>${arr.credit.paymentSchedule[i].remainingDebt}</td>
        </tr>
              `;
        
      } 

      setTableData(arr.credit.paymentSchedule);
    
    }); */
  }, []);

  const handleSortingChange = (column) => {
    const sortOrder = column === sortField && order === "asc" ? "desc" : "asc";
    setSortField(column);
    setOrder(sortOrder);
    handleSorting(column, sortOrder);
  };

  const handleSorting = (sortField, sortOrder) => {
    if (sortField) {
     const sorted = [...tableData].sort((a, b) => {
      if (a[sortField] === null) return 1;
      if (b[sortField] === null) return -1;
      if (a[sortField] === null && b[sortField] === null) return 0;
      return (
       a[sortField].toString().localeCompare(b[sortField].toString(), "en", {
        numeric: true,
       }) * (sortOrder === "asc" ? 1 : -1)
      );
     });
     setTableData(sorted);
    }
   };

  return (
    <>
      <table className="payment_table">
        <thead className="payment_header">
          <tr>
            {columns.map(({ label, accessor }) => {
              return <th key={accessor} onClick={() => handleSortingChange(accessor)}>{label}</th>;
            })}
          </tr>
        </thead>
        <tbody id="table">
          {tableData.map((data) => {
            return (
              <tr key={data.id}>
                {columns.map(({ accessor }) => {
                  const tData = data[accessor] ? data[accessor] : "0";
                  return <td key={accessor}>{tData}</td>;
                })}
              </tr>
            );
          })}
        </tbody>
      </table>
    </>
  );
};

export default Table;
