import React, {useRef, useState} from 'react'
import {useForm} from "react-hook-form";

function Support() {
  const ref = useRef(null);
  let local = true;

  const {
    register,
    handleSubmit,
    formState: { errors },
    reset,
    } = useForm({
    mode: "onBlur"
    });

    const onSubmit = (data) => {
      fetch('http://localhost:8080/email', {  
        method: 'POST', 
        body: JSON.stringify(data) 
      })
      .catch(err => {
          local = false;
          if (local == true){
            ref.current.innerHTML = "<p>You are already subscribed to the bank's newsletter</p>";
          } else if (local == false){
            reset();
          }
        })




    }




  return (
    <>


    <p className="title-support">Support</p>
    <p className="subtitle-support">Subscribe Newsletter & get</p>
    <p className="bank-news">Bank News</p>

    <form onSubmit={handleSubmit(onSubmit)} className='support-form' ref={ref}>
    <label >
    <input className="email" type="email" placeholder="Your email" {...register('email', { required: true})} />
    </label>
    <input id="btn-support" className="btn-support" type="submit" value="Subscribe"/>
    <div >{errors?.email && <p>Enter your email</p>}</div>
      </form>
    </>
  )
}

export default Support
