import React, { useState, useEffect } from "react";

function Table() {
  const pathname = window.location.pathname;
  let id = pathname.split("/");
  id = id[2];

  const [payments, setPayment] = useState({});

  useEffect(() => {
    fetch(`http://localhost:8080/admin/application/${id}`, {
      method: "GET",
      headers: {
        "Content-Type": "application/json",
      },
    })
      .then((response) => response.json())
      .then((data) => setPayment(data));
  }, []);
  const column = [
    { heading: "NUMBER"},
    { heading: "DATE"},
    { heading: "TOTAL PAYMENT"},
    { heading: "INTEREST PAYMENT"},
    { heading: "DEBT PAYMENT"},
    { heading: "REMAINING DEBT"}
  ];

  /* payments['credit']['paymentSchedule']; */
  return (
    <>
      <table>
        <thead>
          <tr>
            {column.map((item, index) => 
              <TableHeadItem item={item} />
            )}
          </tr>
        </thead>
        <tbody>
        {payments.map((item, index) => 
             <TableRow item={item}/>
            )}
        </tbody>
      </table>
    </>
  );
}

const TableHeadItem = ({ item }) => <th>{item.heading}</th>;
const TableRow = ({ item }) => <th>{item.heading}</th>;
export default Table;
