import React from 'react'
import {useForm} from "react-hook-form";

function Support() {

  const {
    register,
    handleSubmit,
    formState: { errors },
    reset,
    } = useForm({
    mode: "onBlur"
    });
    const { data, loading } = this.state;

    const onSubmit = (data) => {
      fetch('http://localhost:8080/email', {  
        method: 'POST', 
        body: JSON.stringify(data) 
      })
      .catch(err => {
        console.log(err.message);

      })
      reset();

    }


  return (
    <>
    <p className="title-support">Support</p>
    <p className="subtitle-support">Subscribe Newsletter & get</p>
    <p className="bank-news">Bank News</p>

    <form onSubmit={handleSubmit(onSubmit)} className='support-form'>
    <label>
    <input className="email" type="email" placeholder="Your email" {...register('email', { required: true})} />
    </label>
    <input id="btn-support" className="btn-support" type="submit" value="Subscribe"/>
    <div >{errors?.email && <p>Enter your email</p>}</div>
    {loading ? <LoadingSpinner /> : <ResultsTable results={data} />}
      </form>
    </>
  )
}

export default Support
