import React, { useState, useEffect } from "react";

const Table=()=> {
  const pathname = window.location.pathname;
  let id = pathname.split("/");
  id = id[2];


  useEffect(() => {
   
    fetch(`http://localhost:8080/admin/application/${id}`, {
      method: "GET",
      headers: {
        "Content-Type": "application/json",
      },
    })
      .then((response) => response.json())
      .then((arr) => {
        for (let i = 0; i < arr.credit.paymentSchedule.length; i++) {
        document.querySelector("#table").innerHTML += `
        <tr>
              <td>${arr.credit.paymentSchedule[i].number}</td>
              <td>${arr.credit.paymentSchedule[i].date}</td>
              <td>${arr.credit.paymentSchedule[i].totalPayment}</td>
              <td>${arr.credit.paymentSchedule[i].interestPayment}</td>
              <td>${arr.credit.paymentSchedule[i].debtPayment}</td>
              <td>${arr.credit.paymentSchedule[i].remainingDebt}</td>
        </tr>
              `;
      } });
   
  }, []);

  const sortTable = (n) => {
    const getCellValue = (tr, idx) => tr.children[idx].innerText || tr.children[idx].textContent;

const comparer = (idx, asc) => (a, b) => ((v1, v2) => 
    v1 !== '' && v2 !== '' && !isNaN(v1) && !isNaN(v2) ? v1 - v2 : v1.toString().localeCompare(v2)
    )(getCellValue(asc ? a : b, idx), getCellValue(asc ? b : a, idx));

// do the work...
document.querySelectorAll('th').forEach(th => th.addEventListener('click', (() => {
    const table = th.closest('table');
    Array.from(table.querySelectorAll('tr:nth-child(n+2)'))
        .sort(comparer(Array.from(th.parentNode.children).indexOf(th), this.asc = !this.asc))
        .forEach(tr => table.appendChild(tr) );
})));
  }


  return (
   <>
    <table className="payment_table">
      <thead className="payment_header">
        <tr>
            <th onClick={sortTable(0)}>NUMBER</th>
            <th onClick={sortTable(1)}>DATE</th>
            <th onClick={sortTable(2)}>TOTAL PAYMENT</th>
            <th onClick={sortTable(3)}>INTEREST PAYMENT</th>
            <th onClick={sortTable(4)}>DEBT PAYMENT</th>
            <th onClick={sortTable(5)}>REMAINING DEBT</th>
        </tr>
      </thead>
     <tbody id="table">

     </tbody>
    </table>

   </>
  );
}


export default Table;
